import { useEffect, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faDroplet,
  faEye,
  faCloudRain,
  faTemperatureThreeQuarters,
  faMagnifyingGlass,
  faSun,
} from "@fortawesome/free-solid-svg-icons";
import "./App.css";

function App() {
  const [data, changeData] = useState("");
  const [city, changeCity] = useState("Miami");
  const [location, changeLocation] = useState("");
  const [condition, changeCondition] = useState("");

  const getData = async () => {
    const info = await fetch(
      `http://api.weatherapi.com/v1/current.json?key=77f552816a9043c686941503221012&q=${city}`
    );
    const response = await info.json();
    changeData(response.current);
    changeLocation(response.location);
    changeCondition(response.current.condition);
  };

  const setCity = () => {
    let city = document.querySelector(".city");
    changeCity(city.value);
    city.value = "";
  };

  const searchToggle = () => {
    let input = document.querySelector(".city-search").style;
    if (input.display === "none") {
      input.display = "initial";
    } else {
      input.display = "none";
    }
    console.log(input.display);
  };

  useEffect(() => {
    getData();
  }, []);

  useEffect(() => {
    getData();

    let uvScale = document.querySelector("#uv-div").lastElementChild.innerHTML;
    let uv = document.querySelector(".uv").innerHTML;
    console.log(uvScale);
    if (uv <= 2) {
      uvScale = "Low";
    } else if (uv >= 3 && uv <= 7) {
      uvScale = "Moderate";
    } else {
      uvScale = "High";
    }
  }, [city]);

  return (
    <div className="app-div">
      <div className="weather-header">
        <h2 onClick={searchToggle}>
          {location.name}{" "}
          <FontAwesomeIcon icon={faMagnifyingGlass} className="search" />
        </h2>
        <div className="city-search">
          <input type="text" className="city" />
          <button className="change-city" onClick={setCity}>
            Get
          </button>
        </div>
        <p className="temp">{data.temp_f}˚</p>
      </div>
      <div className="other-weather">
        <div id="humidity-div">
          <h3>
            Humidity <FontAwesomeIcon icon={faDroplet} />
          </h3>
          <p>{data.humidity}%</p>
        </div>
        <div id="feels-div">
          <h3>
            Feels Like <FontAwesomeIcon icon={faTemperatureThreeQuarters} />
          </h3>
          <p>{data.feelslike_f}˚</p>
        </div>
        <div id="visible-div">
          <h3>
            Visibility <FontAwesomeIcon icon={faEye} />
          </h3>
          <p>{data.vis_miles} mi</p>
        </div>
        <div id="precipitation-div">
          <h3>
            Precipitation <FontAwesomeIcon icon={faCloudRain} />
          </h3>
          <p>{data.precip_in}"</p>
        </div>
        <div id="uv-div">
          <h3>
            UV Index <FontAwesomeIcon icon={faSun} />
          </h3>
          <p className="uv"> {data.uv}</p>
          <p>Scale</p>
        </div>
      </div>
      <img src={condition.icon} alt="day icon" />
    </div>
  );
}

export default App;
