import { useEffect, useState } from "react";
import "./App.css";

function App() {
  const [data, changeData] = useState("");
  const [city, changeCity] = useState("Miami");
  const [location, changeLocation] = useState("");
  const [condition, changeCondition] = useState("");

  const getData = async () => {
    const info = await fetch(
      `http://api.weatherapi.com/v1/current.json?key=77f552816a9043c686941503221012&q=${city}`
    );
    const response = await info.json();
    changeData(response.current);
    changeLocation(response.location);
    changeCondition(response.current.condition);
  };

  const setCity = () => {
    let city = document.querySelector(".city");
    changeCity(city.value);
    city.value = "";
  };

  useEffect(() => {
    getData();
  }, []);

  useEffect(() => {
    getData();
  }, [city]);

  return (
    <div>
      <h1>Today's Weather </h1>
      <div className="city-search">
        <input
          type="text"
          className="city"
          onKeyPress={(e) => {
            if (e.target.key == "Enter") setCity();
          }}
        />
        <button className="change-city" onClick={setCity}>
          Get
        </button>
      </div>
      <div className="weather-header">
        <h2>{location.name}</h2>
        <p className="temp">{data.temp_f}˚</p>
      </div>
      <div className="other-weather">
        <div id="humidity-div">
          <h3>Humidity</h3>
          <p>{data.humidity}%</p>
        </div>
        <div id="feels-div">
          <h3>Feels Like</h3>
          <p>{data.feelslike_f}˚</p>
        </div>
        <div id="visible-div">
          <h3>Visibility</h3>
          <p>{data.vis_miles} mi</p>
        </div>
        <div id="precipitation-div">
          <h3>Precipitation</h3>
          <p>{data.precip_in}"</p>
        </div>
      </div>
      <img src={condition.icon} alt="day icon" />
    </div>
  );
}

export default App;
